/* ==UserStyle==
@name         Github Catppuccin
@version      20220213.18.29
@namespace    userstyles.world/user/arcofee
@description  Soothing pastel theme for GitHub
@author       arcofee
@license      MIT
==/UserStyle== */

@-moz-document domain("github.com") {
/* name: Catppuccin */
/* author: Pocco81 (https://github.com/Pocco81)
           Andreas Grafen (https://github.com/andreasgrafen) */
/* license: MIT */
/* upstream: https://github.com/catppuccin/github */
/* blurb: Soothing pastel theme for the high-spirited! */

:root {
    /* Give H, S, L values */

	/* rainbow 🌈 */
    --rosewater: 10, 56%, 91%;
    --flamingo: 0, 59%, 88%;
    --mauve: 279, 70%, 83%;
    --pink: 316, 72%, 86%;
    --maroon: 349, 60%, 77%;
    --red: 342, 79%, 75%;
    --peach: 024, 88%, 78%;
    --yellow: 041, 88%, 84%;
    --green: 128, 58%, 79%;
    --green_darken: 128, 58%, 76%;
    --green_darkest: 128, 58%, 72%;
    --teal: 171, 53%, 81%;
    --blue: 207, 93%, 79%;
    --blue_darken: 213, 84%, 78%;
    --sky: 189, 71%, 73%;
    --lavender: 238, 100%, 89%;

	/* black & white ☯️ */
    --black0: 254, 25%, 10%;
    --black1: 249, 23%, 12%;
    --black2: 240, 21%, 15%;
    --black3: 249, 18%, 22%;
    --black4: 254, 12%, 36%;
    --gray0: 247, 8%, 46%;
    --gray1: 274, 11%, 59%;
    --gray2: 285, 10%, 75%;
    --white: 220, 38%, 89%;
}

:root {
    /* INTERNAL VARS */
