/* button group seperator */
.BtnGroup-parent:not(:first-child) .BtnGroup-item:not(:focus):not(:hover),
.BtnGroup-item:not(:focus):not(:hover) + .BtnGroup-item:not(:focus):not(:hover) { border-left-color: var(--__black1) !important; }
.unstarred.BtnGroup-parent:not(:first-child) > .BtnGroup-item:not(:focus):not(:hover) { border-left-color: var(--color-btn-border) !important; }
}

