    [style="background-image: linear-gradient(61.63deg, rgb(45, 66, 255) -15.05%, rgb(156, 99, 250) 104.96%);"] {
      background-image: linear-gradient(
        61.63deg,
        @blue -15.05%,
        @mauve 104.96%
