    }

    // who can reply? bg
    .r-rgqbpe {
      background-color: fade(@blue, 10%);
    }

    // circles
    .r-s224ru {
      background-color: @green;
    }

    .r-h7o7i8 {
      background-color: fade(@green, 10%);
    }

    // live indicator
    .r-4nw3r4,
